import { TestBed, ComponentFixture, fakeAsync, tick } from '@angular/core/testing';
import { PostsComponent } from './posts.component';
import { Router } from '@angular/router';
import { MatSnackBar } from '@angular/material/snack-bar';
import { PostService } from '@services/post-service.service';
import { of, throwError } from 'rxjs';
import { PostRequest } from '@models/postRequest.model';

describe('PostsComponent', () => {
  let component: PostsComponent;
  let fixture: ComponentFixture<PostsComponent>;
  let router: jasmine.SpyObj<Router>;
  let snackBar: jasmine.SpyObj<MatSnackBar>;
  let postService: jasmine.SpyObj<PostService>;

  beforeEach(async () => {
    const routerSpy = jasmine.createSpyObj('Router', ['navigateByUrl']);
    const snackBarSpy = jasmine.createSpyObj('MatSnackBar', ['open']);
    const postServiceSpy = jasmine.createSpyObj('PostService', ['createpost', 'createConceptPost']);

    await TestBed.configureTestingModule({
      declarations: [PostsComponent],
      providers: [
        { provide: Router, useValue: routerSpy },
        { provide: MatSnackBar, useValue: snackBarSpy },
        { provide: PostService, useValue: postServiceSpy }
      ]
    }).compileComponents();

    fixture = TestBed.createComponent(PostsComponent);
    component = fixture.componentInstance;
    router = TestBed.inject(Router) as jasmine.SpyObj<Router>;
    snackBar = TestBed.inject(MatSnackBar) as jasmine.SpyObj<MatSnackBar>;
    postService = TestBed.inject(PostService) as jasmine.SpyObj<PostService>;
    fixture.detectChanges();
  });

  it('should create the component', () => {
    expect(component).toBeTruthy();
  });

  it('should process add post successfully', fakeAsync(() => {
    const postRequest = new PostRequest('Title', 'Content', 'author');
    postService.createpost.and.returnValue(of({}));
    component.processAdd(postRequest);
    tick();
    expect(postService.createpost).toHaveBeenCalledWith(postRequest);
    expect(snackBar.open).toHaveBeenCalledWith('Post Made succesfully!', 'Close', { duration: 3000 });
    expect(router.navigateByUrl).toHaveBeenCalledWith('/homepage');
  }));
});
